#include <bits/stdc++.h>
#include <cstdio>
#define INF 0x3f3f3f3f3f3f3f3f
#define IINF 0x3f3f3f3f

using namespace std;

inline long long read() {
  long long x = 0;
  int f = 1;
  char ch = getchar();
  while (ch < '0' || ch > '9') {
    if (ch == '-')
      f = -1;
    ch = getchar();
  }
  while (ch >= '0' && ch <= '9') {
    x = (x << 1) + (x << 3) + (ch ^ 48);
    ch = getchar();
  }
  return x * f;
}
template <typename T> inline void write(T x, char ch = '\n', int tim = 0) {
  if (x < 0ll) {
    putchar('-');
    x = -x;
  }
  if (x > 9ll)
    write(x / 10ll);
  putchar(x % 10ll + '0');
  if (tim) {
    for (int i = 1; i <= tim; ++i) {
      putchar(ch);
    }
  }
}

const long long maxN = 1090;
long long totN;
long long nums[maxN];
bool flag = 1;

long long root;
long long deg[maxN];
long long DP[maxN];
struct Edge {
  long long nxt;
  long long to;
} edges[maxN];
long long cnt_edges;
long long head[maxN];
void add_edge(long long u, long long v) {
  ++cnt_edges;
  edges[cnt_edges].nxt = head[u];
  head[u] = cnt_edges;
  edges[cnt_edges].to = v;
}

void Dfs(int x, int fa) {
  if (deg[x] == 1) {
    DP[x] = nums[x];
    return;
  }
  int sum = 0, maxx = 0;
  for (int i = head[x]; i && flag; i = edges[i].nxt) {
    int v = edges[i].to;
    if (v == fa)
      continue;
    Dfs(v, x);
    sum += DP[v];
    maxx = max((long long)maxx, DP[v]);
  }
  if (!flag)
    return;
  int y = sum - nums[x];
  DP[x] = sum - y * 2;
  if (DP[x] > nums[x]) {
    flag = 0;
    return;
  }
  if (min(sum / 2, sum - maxx) < y) {
    flag = 0;
    return;
  }
  if (x == root && DP[x] != 0) {
    flag = 0;
    return;
  }
}

int main() {
  totN = read();
  for (int i = 1; i <= totN; ++i) {
    nums[i] = read();
  }
  for (int i = 1, u, v; i <= totN - 1; ++i) {
    u = read();
    v = read();
    add_edge(u, v);
    add_edge(v, u);
    ++deg[u];
    ++deg[v];
  }
  if (totN == 2) {
    if (nums[1] == nums[2]) {
      puts("YES");
    } else {
      puts("NO");
    }
  }
  for (int i = 1; i <= totN; ++i) {
    if (deg[i] > 1) {
      root = i;
      break;
    }
  }
  Dfs(root, 0);
  if (flag) {
    puts("YES");
  } else {
    puts("NO");
  }
  return 0;
} // Thomitics Code